import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { AuthService } from 'src/app/services/auth/auth.service';
import { CarritoService } from 'src/app/services/carrito/carrito.service';
import { PersonaService } from 'src/app/services/persona.service';

@Component({
  selector: 'app-carrito',
  templateUrl: './carrito.component.html',
  styleUrls: ['./carrito.component.css']
})
export class CarritoComponent implements OnInit {
  constructor(
    private carritoService:CarritoService,
    private personaService:PersonaService
  ){
  }

  ngOnInit(): void {
    this.costo$ = this.carritoService.getTotal();
  }

  fechaEntrega!:Date;
  vestimentaList:any[]=this.carritoService.getVestimentas();
  costo$:Observable<number>=new Observable();

  removeVestimenta(vestimentaItem:any){
    this.carritoService.removeVestimenta(vestimentaItem);
  }

  reservar(){
    let total=this.costo$;
    let persona:any;
    let tallaDtoList:any;
    this.personaService.findByCorreo(String(sessionStorage.getItem('correoPersona'))).subscribe(
      (personaGotten:any)=>{
        persona=personaGotten;
      }
    )
    if(persona!=null){
      let reserva={
        idPersona:persona.id,
        tallaDtoList,
        total,
      }
    }
  }





}
